// components/QuickActions/QuickActions.jsx
import React from 'react';
import styles from './QuickActions.module.css';

const QuickActions = ({ language }) => {
  const actions = {
    en: [
      { icon: 'üí∏', title: 'Send Money', subtitle: 'UPI/NEFT/IMPS' },
      { icon: 'üì±', title: 'Recharge', subtitle: 'Prepaid/DTH' },
      { icon: 'üí°', title: 'Pay Bills', subtitle: 'Electricity/Water' },
      { icon: 'üè¶', title: 'Banking', subtitle: 'Accounts/Deposits' }
    ],
    hi: [
      { icon: 'üí∏', title: '‡§™‡•à‡§∏‡•á ‡§≠‡•á‡§ú‡•á‡§Ç', subtitle: 'UPI/NEFT/IMPS' },
      { icon: 'üì±', title: '‡§∞‡§ø‡§ö‡§æ‡§∞‡•ç‡§ú', subtitle: '‡§™‡•ç‡§∞‡•Ä‡§™‡•á‡§°/DTH' },
      { icon: 'üí°', title: '‡§¨‡§ø‡§≤ ‡§≠‡§∞‡•á‡§Ç', subtitle: '‡§¨‡§ø‡§ú‡§≤‡•Ä/‡§™‡§æ‡§®‡•Ä' },
      { icon: 'üè¶', title: '‡§¨‡•à‡§Ç‡§ï‡§ø‡§Ç‡§ó', subtitle: '‡§ñ‡§æ‡§§‡•á/‡§ú‡§Æ‡§æ' }
    ]
  };

  return (
    <section className={styles.section}>
      <h2 className={styles.sectionTitle}>
        {language === 'en' ? 'Quick Actions' : '‡§§‡•ç‡§µ‡§∞‡§ø‡§§ ‡§ï‡§æ‡§∞‡•ç‡§Ø'}
      </h2>
      <div className={styles.grid}>
        {actions[language].map((action) => (
          <div key={action.title} className={styles.card}>
            <span className={styles.icon}>{action.icon}</span>
            <h3>{action.title}</h3>
            <p>{action.subtitle}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default QuickActions;
